import { createSlice } from "@reduxjs/toolkit";
import data from "../../epicure.json"

const getData =  fetch("http://localhost:8000/epicure")
        .then((response) => response.json())
        .then((data) => {
        console.log(data)
        return data
        // setStudents(data.studentsData);
});

const allRest = () => {
    getData.then((a) => {
      console.log(a);
    });
  };
  
  allRest();

export const restaurantsSlice = createSlice({
    name: "restaurants",
    initialState: {
      value: allRest(),
    },
    reducers: {
      restaurantPageFilter: (state, action) => {
      const filterType = action.payload;
        switch (filterType){
          case "all":
              state.value = allRest() 
              break;
          case "new":
              state.value = allRest()
              state.value = state.value.filter(
                  (restaurant) => restaurant.isNew === true); 
              break;
          case "mostPopular":
              state.value = data.restaurants 
              state.value = state.value.filter(
                  (restaurant) => restaurant.isPopular === true); 
              break;
          case "openNow":
              state.value = data.restaurants 
              state.value = state.value.filter(
                  (restaurant) => restaurant.isOpen === true); 
              break;
          case "mapView":
              state.value = data.restaurants 
              state.value = state.value.filter(
                  (restaurant) => restaurant.isNew === true); 
              break;
          
          default:
          state.value = state.value;
        }
      }
  }
  })
  
  export const { restaurantPageFilter } = restaurantsSlice.actions;
  export default restaurantsSlice.reducer;
  

