import search from '../../../assets/NavbarAssets/search.svg';
import "../Search_nav/SearchNav.css";
import { useState, useRef, useEffect } from "react";


const SearchNav: React.FC = () => {
const showSearchInput  = useState<boolean>(false);  
const showSearchString  = useRef<string>("");  

const setshowSearchInput = (event: React.MouseEvent<HTMLElement>) => {
    event.preventDefault()
    console.log("helllo")
    showSearchInput.current = !showSearchInput.current
    console.log(showSearchInput)
} 

useEffect(() => {
  showSearchString.current= showSearchInput.current ? 'show' : 'not';
}, [showSearchInput.current])

    return (
        <form id="button-box">
            <input type="text" placeholder={`Search for restaurant cuisine, chef`} className={showSearchString.current}/>
            <button type="submit"><img src={search} alt="search" id="button-icon" onClick={setshowSearchInput} /></button>
        </form> 
    )
}


export default SearchNav
