import React from "react";
import "./Card_restaurant.css"

interface Iprops {
    class?: string,
    src?: string,
    alt?:string,
    RestaurantName?: string,
    chef?:string,
    rating?:string
}

const CardRestaurant: React.FC<Iprops> = (props:Iprops)=> {
    return (
        <div className={`card ${props.class}`} >
            <img src={props.src} alt={props.alt} className="img-rest-card"/>
            <div>{props.RestaurantName} class="name-rest-card"</div>
            <div>{props.chef}</div>
            <img src={props.rating} alt="" />
        </div>
    ) 
}

export default CardRestaurant
