import React from "react";
import data from "../../epicure.json"
import CardRestaurant from "../Card_restaurant/Card_restaurant";
import "./All_restaurants.css"
import vector from "../../assets/icons/vector.svg"
// import stars from "../../assets/icons/star4.svg"
// import img from "../../assets/images/Restaurants/claro-1.png"


const AllRestaurants: React.FC = () => {
const rests = data.restaurants.map((rest:any, index: number) => {
    // if (rest.id === 1) {
        return <CardRestaurant 
        class= {rest.name}
        // id= {index}
        // src= {require(`../../assets/images/Restaurants/${rest.img}`)}
        RestaurantName = {rest.name}
        chef = {rest.chef}
        rating = {require(`../../assets/icons/star${rest.rating}.svg`)} 
        />
    
//     } else {
//         return null;
//  }   
})
    return (
        
        <div className="all-rest-container" >
            <div className="popular-rest-title"></div>
            <div className="all-rest-popular">
                {rests}
            </div>
            <button className="all-rest-path">
                All Restaurants <img src={vector} alt="vector" className="vector-rest"/>
            </button>
        </div>
    ) 
}    


export default AllRestaurants
