import { createSlice } from "@reduxjs/toolkit";
import data from "../../epicure.json"

export const dishesSlice = createSlice({
  name: "dishes",
  initialState: {
    value: data.dishes,
  },
  reducers: {
    dishesPageFilter: (state,action) => {
      const dishesFilterType = action.payload;
      switch (dishesFilterType){
        case "all":
          state.value = data.dishes
        case "breakfast":
          state.value = data.dishes
          state.value = state.value.filter(
            (dish) => dish.isBreakfast === true);
          break;
        case "lanch":
          state.value = data.dishes
          state.value = state.value.filter(
            (dish) => dish.isLanch === true);
          break;
        case "dinner":
          state.value = data.dishes
          state.value = state.value.filter(
            (dish) => dish.isBreakfast === true);
          break;
          
      }
    }
  },
});


export default dishesSlice.reducer;
