import React from "react";
import "./Hero.css"


const Hero: React.FC = ()=> {
    return (
        <div id="hero-container">
                <div id="hero-search">
                    <p id="hero-search-text">Epicure works with the top chef restaurants in Tel Aviv</p> 
                    <div id="hero-search-input">
                        <input type="text" placeholder="Search for restaurant cuisine, chef" id="hero-input-text"/>
                    </div>

                </div>            
        </div>
    ) 
}

export default Hero as React.FC;


const ChangeOpacity = () => {
  const [opacity, setOpacity] = useState(1);

  const handleClick = () => {
    setOpacity(opacity === 1 ? 0.5 : 1);
  };

  return (
    <div>
      <p style={{ opacity }} onClick={handleClick}>
        Click here to change opacity
      </p>
    </div>
  );
};
