// import srcImg from "../../../assets/images/Restaurants/claro-1.png"

const Popular_restaurant: React.FC = ()=> {
// const srcImg = require(`${data.restaurants[0].img}`)
// console.log(data.restaurants[0].img)
// console.log(srcImg)
const items = data.restaurants.map((item:any) => {
    if (item.isPopular === true) {
        return <CardRestaurant 
        src="require()"
        RestaurantName = {item.name}
        chef = {item.chef}
        rating = {item.rating} />
    
    } else {
        return null;
 }   
})
    return (
        <div>
            {items}
        </div>
    ) 
}

