import { useParams } from "react-router-dom";
import dishImg from "../../../assets/images/Dishes/garbanzo-frito.png"

const OneRestDetails: React.FC = ()=> {
    const allRestaurants = useSelector(
            (state:IRootState) => state.restaurants.value
