import search from '../../../assets/NavbarAssets/search.svg';
import "../Search_nav/SearchNav.css";
import { useState, useRef } from "react";



const SearchNav: React.FC = () => {
const showSearchInput  = useRef<boolean>(false);  
const setshowSearchInput = (event) => {
    event?.preventDefault
    showSearchInput.current = !showSearchInput
} 
    return (
        <form id="button-box">
            <input type="text" placeholder={`Search for restaurant cuisine, chef`} className={showSearchInput ? 'search-text show' : 'search-text'}/>
            <button type="submit"><img src={search} alt="search" id="button-icon" onClick={setshowSearchInput (event)} /></button>
        </form> 
    )
}


export default SearchNav
