import React from "react";
import search from '../../../assets/NavbarAssets/search.svg';
import "../Search_nav/SearchNav.css";
import { useState } from "react";



const SearchNav: React.FC = () => {
const [showSearchInput, setshowSearchInput] = useState<boolean>(false);   
    return (
        <form id="button-box">
            <input type="text" placeholder={`Search for restaurant cuisine, chef`} className={showSearchInput ? 'search-text show' : 'search-text'}/>
            <button type="submit"><img src={search} alt="search" id="button-icon" onClick={() => setshowSearchInput(!showSearchInput)}/></button>
        </form> 
    )
}


export default SearchNav
