import { createSlice } from "@reduxjs/toolkit";
import data from "../../epicure.json"

export const dishesSlice = createSlice({
  name: "dishes",
  initialState: {
    value: data.dishes,
    valueRestDishes: [],
  },
  reducers: {
    dishesPageFilter: (state,action) => {
       action.payload;
      switch (action.payload.type){
        case "all":
          state.value = action.payload.data;
          state.valueRestDishes =  action.payload.data;
          break;
        case "breakfast":
          state.value = data.dishes
          state
          valueRestDishes = action.payload.data.filter(
            (dish) => dish.isBreakfast === true);
          break;
        case "lanch":
          state.value = data.dishes
          state.value = state.value.filter(
            (dish) => dish.isLanch === true);
          break;
        case "dinner":
          state.value = data.dishes
          state.value = state.value.filter(
            (dish) => dish.isDinner === true);
          break;

      default:
        state.value = data.dishes
      }
    }
  },
});

export const { dishesPageFilter } = dishesSlice.actions;
export default dishesSlice.reducer;
